package service;

import model.Student;
import service.IService.IStudentService;

import java.util.ArrayList;
import java.util.List;

public class StudentService implements IStudentService<Student> {
    List<Student> students = new ArrayList<>();

    public StudentService() {
        this.students.add(new Student(1, 20, "Nguyễn Anh Tuấn", "data:image/jpeg;base64,"));
        this.students.add(new Student(2, 12, "Trần Văn Sơn  ", "data:image/jpeg;base64,"));
    }

    @Override
    public void add(Student student) {
        this.students.add(student);
    }

    @Override
    public int findByIndex(int id) {
        for (int i = 0; i < this.students.size(); i++) {
            if (id == students.get(i).getId()) {
                return i;
            }
        }
        return -1;
    }

    @Override
    public void delete(int id) {
        int index = this.findByIndex(id);
        this.students.remove(index);
    }

    @Override
    public List<Student> findAll() {
        return this.students;
    }

    @Override
    public void edit(int id, Student student) {
        int index = this.findByIndex(id);
        this.students.set(index, student);
    }
}
